import React from 'react';
import logo from './logo.svg';
import './App.css';



// var destination = document.querySelector("#root");


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      height: "",
      weight: "",
      result: ""
    }

    this.handleClick = this.handleClick.bind(this)
  }



  handleClick(event) {
        event.preventDefault()
        const {name, value} = event.target
        this.setState({ [name]: value })



        //bmi function//



   /*  let newHeight = parseFloat(height / 100)
    let bmi = weight / (newHeight * newHeight) */
 }


 handleClear(event) {
        event.target.reset()
 }




  render() {
    return ( 
      <main>
        <section id="container"> 
          <h2>BMI Calculator</h2>
            <form>
              <p div="height">Height (95cm-220cm)</p>
                <input 
                  type="number" 
                  name="height" 
                  onKeyDown="return false" 
                  step="0.1" 
                  placeholder="cm" 
                  min="95" 
                  max="220"
                  value = {this.state.height}
                />

         <br />

               <p id="weight">Weight (10kg-300kg)</p>
                 <input 
                   type="number" 
                   name="weight" 
                   onKeyDown="return false" 
                   step="0.1" 
                   placeholder="kg" 
                   min="10" 
                   max="300"
                   value = {this.state.weight}
               />

          <br />


        <button id="calculate" onClick={this.handleClick}>Calculate</button>

        <br />

        <button id="clear" onClick={this.handleClear}>Clear</button>

        <p id="score_one">Result</p>
        <p id="score_two">Your BMI is {this.state.result}</p>

     </form>
    </section>
   </main>
  )
 }
}


// ReactDOM.render((
