import React from 'react'
import './App.css'


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      height: "",
      weight: "",
      bmiResult: "",
      bmiClass: ""
    }
  }



  handleHeightChange(event) {
        this.setState({ height: event.target.value})
  }


  handleWeightChange(event) {
        this.setState({ weight: event.target.value})
  }


  handleKeyPress(source, event) {
      const allowedChars = ".0123456789"
      const currentChar = event.key
      let found = false
      for (let i = 0;  i < allowedChars.length; i++) {
        if (currentChar === allowedChars[i]) {
          found = true
        }
      }
      if(found === false) {
        event.preventDefault()
        return
      }

      //limiting character input

      let currentValue = 0;    
      if (source === 'height') {
        currentValue = parseInt(this.state.height + currentChar);
        if (currentValue > 220) {
          event.preventDefault()
        }
      } else {
        currentValue = this.state.weight + currentChar;
        if (currentValue > 300) {
          event.preventDefault()
        }
      }

  }


  //calculating BMI

    calcBmi() {
      let result = ((this.state.weight / this.state.height / this.state.height) * 10000).toFixed(2)
      this.setState({result: bmiResult, bmiClass: this.classBmi(bmiResult)})
    }

    classBmi(bmiResult) {
      if(bmiResult < 18.5)
        return "Your score is `${bmiResult}`. You are underweight."
      if(bmiResult >= 18.5 && bmi <= 24.9) 
        return "Your score is `${bmiResult}`. You are normal."
      if(bmiResult >= 25 && bmi <= 29.9) 
        return "Your score is `${bmiResult}`. You are overweight."
      if(bmiResult >= 30) 
        return "Your score is `${bmiResult}`. You are obese."
    }








  render() {
    return ( 
      <main>
        <section id="container"> 
          <h2>BMI Calculator</h2>
            <form>
              <p div="height">Height (95cm-220cm)</p>
                <input 
                  type="number" 
                  name="height" 
                  step="0.1" 
                  placeholder="cm" 
                  min="95" 
                  max="220"
                  value={this.state.height} //kell?
                  onChange={this.handleHeightChange.bind(this)}
                  onKeyPress={this.handleKeyPress.bind(this, 'height')}

                />

         <br />

               <p id="weight">Weight (10kg-300kg)</p>
                 <input 
                   type="number" 
                   name="weight" 
                   step="0.1" 
                   placeholder="kg" 
                   min="10" 
                   max="300"
                   value={this.state.weight} // kell?
                   onChange={this.handleWeightChange.bind(this)}
                   onKeyPress={this.handleKeyPress.bind(this, 'weight')}


               />

          <br />


        <button id="calculate" onClick={this.calcBmi.bind(this)}>Calculate</button>

        <br />

        <button id="clear" onClick={this.clearBmi.bind(this)}>Clear</button>

        <p id="score_one">Result</p>
        <p id="score_two">{this.classBmi.bind(this)}</p>


     </form>
    </section>
   </main>
  )
 }
}



export default App
