import React from 'react'   //{useRef} : storing a reference to a component
import './App.css'


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      height: "",
      weight: "",
      bmiValue: ""
    }
  }

  // const inputRef = useRef() //new hook

  handleHeightChange(event) {
        this.setState({ height: event.target.value})
  }


  handleWeightChange(event) {
        this.setState({ weight: event.target.value})
  }

    //limiting input to numbers

  handleKeyPress(source, event) {
      const allowedChars = ".0123456789"
      const currentChar = event.key
      let found = false
      for (let i = 0;  i < allowedChars.length; i++) {
        if (currentChar === allowedChars[i]) {
          found = true
        }
      }
      if(found === false) {
        event.preventDefault()
        return
      }

      //limiting number input /height/weight

      let currentValue = 0;    
      if (source === 'height') {
        currentValue = parseInt(this.state.height + currentChar);
        if (currentValue > 220) {
          event.preventDefault()
        }
      } else {
        currentValue = this.state.weight + currentChar;
        if (currentValue > 300) {
          event.preventDefault()
        }
      }

  }


  //calculating BMI and classifying the results

    calcBmi(event) {
      event.preventDefault()

      let bmi=(this.state.weight / (this.state.height/100 * this.state.height/100)).toFixed(1)
      this.setState({bmiValue: bmi}) 

      if(this.state.bmiValue < 18.5)
        console.log(this.state.bmiValue)
      if(this.state.bmiValue >= 18.5 && this.state.bmiValue <= 24.9) 
        console.log(this.state.bmiValue)
      if(this.state.bmiValue >= 25 && this.state.bmiValue <= 29.9) 
        console.log(this.state.bmiValue)
      if(this.state.bmiValue >= 30) 
        console.log(this.state.bmiValue)
    }



    clearBmi(event) {
            event.preventDefault()

      this.setState({height: "", weight: "", bmiValue: ""})
      // ReactDOM.findDOMNode(this.refs.myInput).focus()
      // inputRef.current.
      // event.target.value = null

    }




  render() {
    return ( 
      <main>
        <section id="container"> 
          <h2>BMI Calculator</h2>
            <form>
              <p div="height">Height (95cm-220cm)</p>
                <input 
                  type="number" 
                  name="height" 
                  step="0.1" 
                  placeholder="cm" 
                  min="95" 
                  max="220"
                  value={this.state.height}//kell?
                  onChange={this.handleHeightChange.bind(this)}
                  onKeyPress={this.handleKeyPress.bind(this, 'height')}
                  // ref={inputRef}

                />

         <br />

               <p id="weight">Weight (10kg-300kg)</p>
                 <input 
                   type="number" 
                   name="weight" 
                   step="0.1" 
                   placeholder="kg" 
                   min="10" 
                   max="300"
                   value={this.state.weight} // kell?
                   onChange={this.handleWeightChange.bind(this)}
                   onKeyPress={this.handleKeyPress.bind(this, 'weight')}
                   // ref={inputRef}

               />

          <br />


        <button 
          id="calculate" onClick={this.calcBmi.bind(this)}>Calculate</button>

        <br />

        <button id="clear" onClick={this.clearBmi.bind(this)}>Clear</button>


        <p id="score_one">Result</p>
        <p id="score_two">...</p>


     </form>
    </section>
   </main>
  )
 }
}



export default App
