import React from 'react'
import './App.css'


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      height: "",
      weight: "",
      result: ""
    }
  }



 //        //bmi function//



 //   /*  let newHeight = parseFloat(height / 100)
 //    let bmi = weight / (newHeight * newHeight) */
 // }

  handleHeightChange(event) {
        this.setState({ height: event.target.value})
  }


  handleWeightChange(event) {
        this.setState({ weight: event.target.value})
  }


  handleKeyPress(source, event) {
      const allowedChars = ".0123456789"
      const currentChar = event.key
      let found = false
      for (let i = 0;  i < allowedChars.length; i++) {
        // console.log(`${event.key} !== ${allowedChars[i]} = ${event.key !== allowedChars[i]}` )
        if (currentChar == allowedChars[i]) {
          found = true
        }
      }
      if(found == false) {
        event.preventDefault()
        return
      }

      let currentValue = 0;
      if (source == 'height') {
        currentValue = this.state.height + currentChar;
      } else {
        currentValue = this.state.weight + currentChar;
      }
      console.log(source)
      console.log(currentValue)
  }






  render() {
    return ( 
      <main>
        <section id="container"> 
          <h2>BMI Calculator</h2>
            <form>
              <p div="height">Height (95cm-220cm)</p>
                <input 
                  type="number" 
                  name="height" 
                  step="0.1" 
                  placeholder="cm" 
                  min="95" 
                  max="220"
                  value={this.state.height}
                  onChange={this.handleHeightChange.bind(this)}
                  onKeyPress={this.handleKeyPress.bind(this, 'height')}

                />

         <br />

               <p id="weight">Weight (10kg-300kg)</p>
                 <input 
                   type="number" 
                   name="weight" 
                   step="0.1" 
                   placeholder="kg" 
                   min="10" 
                   max="300"
                   value={this.state.weight}
                   onChange={this.handleWeightChange.bind(this)}
                   onKeyPress={this.handleKeyPress.bind(this, 'weight')}


               />

          <br />


        <button id="calculate" onClick={this.handleChange}>Calculate</button>

        <br />

        <button id="clear" onClick={this.handleClear}>Clear</button>

        <p id="score_one">Result</p>
        <p id="score_two">Your BMI is: {this.state.result}</p>


     </form>
    </section>
   </main>
  )
 }
}



export default App
