import React from 'react'
import './App.css'


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      height: "",
      weight: "",
      result: ""
    }

    // this.handleChange = this.handleChange.bind(this)
    this.handleHeightChange = this.handleHeightChange.bind(this)
    this.handleWeightChange = this.handleWeightChange.bind(this)
  }



 //  handleChange(event) {
 //        event.preventDefault()
 //        const {name, value} = event.target
 //        this.setState({ [name]: value })



 //        //bmi function//



 //   /*  let newHeight = parseFloat(height / 100)
 //    let bmi = weight / (newHeight * newHeight) */
 // }


  handleWeightChange(event) {
        event.preventDefault()
        this.setState({ weight: event.target.value})
  }

  handleHeightChange(event) {
        event.preventDefault()
        this.setState({ height: event.target.value})


  }


  render() {
    return ( 
      <main>
        <section id="container"> 
          <h2>BMI Calculator</h2>
            <form>
              <p div="height">Height (95cm-220cm)</p>
                <input 
                  type="number" 
                  name="height" 
                  step="0.1" 
                  placeholder="cm" 
                  min="95" 
                  max="220"
                  onChange={this.handleHeightChange}
                  value={this.state.height}
                  onKeyDown="return false"
                />

         <br />

               <p id="weight">Weight (10kg-300kg)</p>
                 <input 
                   type="number" 
                   name="weight" 
                   step="0.1" 
                   placeholder="kg" 
                   min="10" 
                   max="300"
                   value={this.state.weight}
                   onChange={this.handleWeightChange}

               />

          <br />


        <button id="calculate" onClick={this.handleChange}>Calculate</button>

        <br />

        <button id="clear" onClick={this.handleClear}>Clear</button>

        <p id="score_one">Result</p>
        <p id="score_two">Your BMI is: {this.state.result}</p>


     </form>
    </section>
   </main>
  )
 }
}



export default App
