import React from 'react'   
import './App.css'



class App extends React.Component {
  constructor() {
    super()
    this.state = {
      height: "",
      weight: "",
      bmiValue: "",
      // defaultChonk: chonk,
      // AllChonkImg: ["./images/default_chonk.jpg", "./images/otherchonk.jpg"],
      chonkVisibility : "invisibleChonk",
      resultChonk: "",
      AllChonkImg: {
        slimChonk: ["./images/slim/1.jpg", "./images/slim/2.jpg", "./images/slim/3.jpg", "./images/slim/4.jpg"],
        normalChonk: ["./images/normal/1.jpg", "./images/normal/2.jpg", "./images/normal/3.jpg", "./images/normal/4.jpg", "./images/normal/5.jpg"],
        fatChonk: ["./images/fat/1.jpg", "./images/fat/2.jpg", "./images/fat/3.jpg", "./images/fat/4.jpg"],
        tooFatChonk: ["./images/2chonk/1.jpg", "./images/2chonk/2.jpg", "./images/2chonk/3.jpg", "./images/2chonk/4.jpg", "./images/2chonk/5.jpg"]
      }
    }

    this.calcBmi = this.calcBmi.bind(this)
    // this.handleChonk = this.handleChonk.bind(this)
  }


  handleHeightChange(event) {
        this.setState({ height: event.target.value})
  }


  handleWeightChange(event) {
        this.setState({ weight: event.target.value})
  }

    //limiting input to numbers

  handleKeyPress(source, event) {
      const allowedChars = ".0123456789"
      const currentChar = event.key
      let found = false
      for (let i = 0;  i < allowedChars.length; i++) {
        if (currentChar === allowedChars[i]) {
          found = true
        }
      }
      if(found === false) {
        event.preventDefault()
        return
      }

      //limiting number input /height/weight
    let currentValue = 0   
          if (source === 'height') {
            currentValue = parseInt(this.state.height + currentChar)
            if (currentValue > 220) {
              event.preventDefault()
            }
          } else {
            currentValue = this.state.weight + currentChar
            if (currentValue > 300) {
              event.preventDefault()
            }
          }


  }


  //calculating BMI and classifying the results

    calcBmi(event) {
      let bmi=(this.state.weight / (this.state.height/100 * this.state.height/100)).toFixed(1)
      this.setState({bmiValue: bmi}) 

      if(this.state.weight == "") {
        return null
      }

      if(this.state.height == "") {
        return null
      }

      if(this.state.bmiValue < 18.5) { //thin
        const randNum = Math.floor(Math.random() * this.state.AllChonkImg.slimChonk.length)
        const randChonk = this.state.AllChonkImg.slimChonk[randNum]
        this.setState({resultChonk: randChonk})
        this.setState({ chonkVisibility: "visibleChonk" })
      } 

      if(this.state.bmiValue >= 18.5 && this.state.bmiValue <= 24.9) { //normal
        const randNum = Math.floor(Math.random() * this.state.AllChonkImg.normalChonk.length)
        const randChonk = this.state.AllChonkImg.normalChonk[randNum]
        this.setState({resultChonk: randChonk})
        this.setState({ chonkVisibility: "visibleChonk" })      
      }

      if(this.state.bmiValue >= 25 && this.state.bmiValue <= 29.9) { //fat
        const randNum = Math.floor(Math.random() * this.state.AllChonkImg.fatChonk.length)
        const randChonk = this.state.AllChonkImg.fatChonk[randNum]
        this.setState({resultChonk: randChonk})
        this.setState({ chonkVisibility: "visibleChonk" })    
      }  

      if(this.state.bmiValue >= 30) { //obese
        const randNum = Math.floor(Math.random() * this.state.AllChonkImg.tooFatChonk.length)
        const randChonk = this.state.AllChonkImg.tooFatChonk[randNum]
        this.setState({resultChonk: randChonk})
        this.setState({ chonkVisibility: "visibleChonk" })
      }

    }


    // clear button
    clearBmi(event) {
      event.preventDefault()
      this.setState({height: "", weight: "", bmiValue: ""})
    }



    // randomizing the images
    // handleChonk(event) {
    //   const randNum = Math.floor(Math.random() * this.state.AllChonkImg.normalChonk.length)
    //   const randChonk = this.state.AllChonkImg.normalChonk[randNum]
    //   this.setState({resultChonk: randChonk})
    //   this.setState({ chonkVisibility: "visibleChonk" })
    // }



  render() {
    return ( 
      <main>
        <section id="container"> 
          <h2>BMI Calculator</h2>
            <form>
              <p div="height">Height (95cm-220cm)</p>
                <input 
                  type="number" 
                  name="height" 
                  step="0.1" 
                  placeholder="cm" 
                  min="95" 
                  max="220"
                  value={this.state.height}//kell?
                  onChange={this.handleHeightChange.bind(this)}
                  onKeyPress={this.handleKeyPress.bind(this, 'height')}

                />

         <br />

               <p id="weight">Weight (10kg-300kg)</p>
                 <input 
                   type="number" 
                   name="weight" 
                   step="0.1" 
                   placeholder="kg" 
                   min="10" 
                   max="300"
                   value={this.state.weight} // kell?
                   onChange={this.handleWeightChange.bind(this)}
                   onKeyPress={this.handleKeyPress.bind(this, 'weight')}

               />

        <br />


       <button 
          id="calculate" 
          onClick={(event) => {
            event.preventDefault()
            this.calcBmi()
          }}
          >Calculate
        </button>


        <br />

        <button id="clear" onClick={this.clearBmi.bind(this)}>Clear</button>


        <p id="score_one">Result</p>
        <p id="score_two">...</p>



     </form>
    <div className={this.state.chonkVisibility}>
    <img src={this.state.resultChonk} alt="" />
    </div>

    </section>
   </main>
  )
 }
}



export default App
