import React from 'react'   
import './App.css'



class App extends React.Component {
  constructor() {
    super()
    this.state = {
      height: "",
      weight: "",
      heightError: "",
      weightError: "",
      heightNr: "",
      weightNr: "",
      bmiValue: "",
      bmiText: "",
      chonkVisibility : "invisibleChonk",
      resultChonk: "",
      AllChonkImg: {
        slimChonk: ["./images/slim/1.jpg", "./images/slim/2.jpg", "./images/slim/3.jpg", "./images/slim/4.jpg"],
        normalChonk: ["./images/normal/1.jpg", "./images/normal/2.jpg", "./images/normal/3.jpg", "./images/normal/4.jpg", "./images/normal/5.jpg"],
        fatChonk: ["./images/fat/1.jpg", "./images/fat/2.jpg", "./images/fat/3.jpg", "./images/fat/4.jpg"],
        tooFatChonk: ["./images/2chonk/1.jpg", "./images/2chonk/2.jpg", "./images/2chonk/3.jpg", "./images/2chonk/4.jpg", "./images/2chonk/5.jpg"]
      }
    }


    this.calcBmi = this.calcBmi.bind(this)
  }

  handleHeightChange(event) {
        this.setState({ height: event.target.value})
  }

  handleWeightChange(event) {
        this.setState({ weight: event.target.value})
  }

  //limiting input to numbers
  handleKeyPress(source, event) {
      const allowedChars = ".0123456789"
      const currentChar = event.key
      let found = false
      for (let i = 0;  i < allowedChars.length; i++) {
        if (currentChar === allowedChars[i]) {
          found = true
        }
      }
      if(found === false) {
        event.preventDefault()
        return
      }

    //limiting number input in height/weight
    let currentValue = 0   
          if (source === 'height') {
            currentValue = parseInt(this.state.height + currentChar)
            if (currentValue > 220) {
              event.preventDefault()
            }
          } else {
            currentValue = this.state.weight + currentChar
            if (currentValue > 300) {
              event.preventDefault()
            }
          }
  }


    //classifying the results w/images, calculating BMI
    classifyResult(result) {
        if (result < 18.5) {
            return "slim"
        }
        if (result <= 24.9) {
          return "normal"
        }
        if (result <= 29.9) {
          return "fat"
        }
        return "tooFat"
    }

    //height, weight validation and min/max man input nrs 
    validate() {
      this.setState({heightError: "", weightError: ""})
      let heightError = ""
      let weightError = ""

      this.setState({heightNr: "", weightNr: ""})
      let heightNr = ""
      let weightNr = ""

      //height, weight need to be filled
      if(!this.state.height) {
        heightError = "enter height"
      }

      if(!this.state.weight) {
        weightError = "enter weight"
      }

      if(heightError || weightError) {
        this.setState({heightError, weightError}) //--> the key is equal to the same name variable; same as heightError: heightError
        return false //because it's not valid
      }
      return true //if we don't hit an error


      if(this.state.height < 95){
        heightNr = "greater than 95"
        console.log(this.state.height)
      }
      if(this.state.height > 220) {
        heightNr = "less than 220"
        console.log(heightNr)
        }


      if(this.state.weight < 10) {
        weightNr = "greater than 10"
        console.log(weightNr)
      } 
      if(this.state.weight > 300) {
        weightNr = "less than 300"
        console.log(weightNr)
        }


      if(heightNr || weightNr) {
        this.setState({heightNr, weightNr})
        return false
      }
      return true
    }


    calcBmi(event) {
      if(!this.validate()) {
        return
      }

      let bmi = (this.state.weight / (this.state.height/100 * this.state.height/100)).toFixed(1)
      let chonks = null
      let resultString = ""


      switch (this.classifyResult(bmi)) {
        case "slim": {
          chonks = this.state.AllChonkImg.slimChonk
          resultString = "You're pretty slonky, have a burger"
          break
        }
        case "normal": {
          chonks = this.state.AllChonkImg.normalChonk
          resultString = "You're okay...for now"
          break
        }
        case "fat": {
          chonks = this.state.AllChonkImg.fatChonk
          resultString = "You're getting kind of fat"
          break
        }
        case "tooFat": {
          chonks = this.state.AllChonkImg.tooFatChonk
          resultString = "You're quite the chonker, there"
          break
        }
      }

      //gettind the random images and avoiding duplicates
      const randNum = Math.floor(Math.random() * chonks.length)
      const randChonk = chonks[randNum]


      if(this.state.resultChonk === randChonk) {
          // console.log("IS EQUAL!")
          this.calcBmi(event)
          return
      }

      this.setState({resultChonk: randChonk})
      this.setState({chonkVisibility: "visibleChonk" })  
      this.setState({bmiText: resultString})
      this.setState({bmiValue: bmi}) 
  }


    // clear button
    clear(event) {
      event.preventDefault()
      this.setState({ height: "", weight: "", bmiValue: "" })
      this.setState({ chonkVisibility: "invisibleChonk" })
      this.setState({ heightError: "", weightError: "" })
    }


  render() {
    return ( 
      <main>
        <section id="container"> 
          <h2>BMI Calculator</h2>
            <form>
              <p div="height">Height (95cm-220cm)</p>
                <input 
                  type="number" 
                  name="height" 
                  step="0.1" 
                  placeholder="cm" 
                  min="95" 
                  max="220"
                  value={this.state.height}//kell?
                  onChange={this.handleHeightChange.bind(this)}
                  onKeyPress={this.handleKeyPress.bind(this, 'height')}
                />
                <div className="he-we-error">{this.state.heightError}</div>
                <div className="hw-minmax-txt">{this.state.heightNr}</div>


         <br />

               <p id="weight">Weight (10kg-300kg)</p>
                 <input 
                   type="number" 
                   name="weight" 
                   step="0.1" 
                   placeholder="kg" 
                   min="10" 
                   max="300"
                   value={this.state.weight} // kell?
                   onChange={this.handleWeightChange.bind(this)}
                   onKeyPress={this.handleKeyPress.bind(this, 'weight')}
               />
                <div className="he-we-error">{this.state.weightError}</div>
                <div className="hw-minmax-txt">{this.state.weightNr}</div>


        <br />


       <button 
          id="calculate" 
          onClick={(event) => {
            event.preventDefault()
            this.calcBmi()
          }}
          >Calculate
        </button>


        <br />

        <button id="clear" onClick={this.clear.bind(this)}>Clear</button>

        <br />



     </form>
    <div className={this.state.chonkVisibility}>
      <img src={this.state.resultChonk} alt="" />
      <h2 className="result-top-text">Your BMI result is {this.state.bmiValue}</h2>
      <h2 className="bmi-text">{this.state.bmiText}</h2>
    </div>

    </section>
   </main>
  )
 }
}



export default App
